import React from 'react'
import Field from './Field'
import './Home.css'
import Fielddetails from './Fielddetails'
import {useState,useEffect} from 'react'
import {Link} from 'react-router-dom'

function Home() {


    // const [showComponent,setShowComponent] = useState(false);
    // const handleClick=()=>{
    //     console.log("Clicked");
    //     setShowComponent(true);
    // }

    //This is to request the server to send the data from the server by posting a query.
    const [data, setData] = useState([]); 

    useEffect(() => {
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },

            body: '{"query": "{allCategories{name,stackImage}}"}'
        };

         fetch('http://127.0.0.1:8000/graphql', sendingPost)
            .then(response => response.json())
            .then(data => setData(data.data.allCategories));
    },[])


    console.log(data);

    //Posting is till here.


    return (
        <div className='home'>
            <img className='landing_image' src='https://image.freepik.com/free-vector/communication-flat-icon_1262-18771.jpg'/>
            <div className='fields'>
                {/* {showComponent? <Fielddetails fieldname='AmHacks'/>:fieldboard} */}
                {
                data.map((field) => (
                    <div className='field-previe' key={field.id}>
                        <Link to="/fielddetails">
                            <Field
                            stack_image='https://p1.hiclipart.com/preview/697/161/312/button-ui-app-one-green-android-icon-png-clipart.jpg'
                            name={field.name}
                            />
                        </Link>
                    </div>
                ))
            }
            </div>
            <div className='notifications'>
                <Fielddetails
                fieldname='AmHacks'/>
            </div>
            <div className='footer'>
            </div>
        </div>
    )
}

export default Home
