import React from 'react'
import Field from './Field'
import './Home.css'
import {useState,useEffect} from 'react'
import {Link} from 'react-router-dom'

function Home() {

    //This is to request the server to send 
    //the data from the server by posting a
    //query.
    const [data, setData] = useState([]); 
    const [searchTerm,setSearchTerm] = useState('')

    useEffect(() => {
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },

            body: '{"query": "{allCategories{id,name,stackImage}}"}'
        };

         fetch('http://127.0.0.1:8000/graphql', sendingPost)
            .then(response => response.json())
            .then(data => setData(data.data.allCategories));
    },[])
    console.log(data);

    //Posting is till here.

    return (
        <div className='home'>
            <div>
                <input type='text' placeholder='Search member' onChange={ event => {setSearchTerm(event.target.value)}}/>
            </div>
            <img className='landing_image' src='https://i.postimg.cc/yxsJ61rG/4380-removebg-preview.png'/>
            <div className='fields'>
                {
// Here thsi filter is for showing data according to the serachbar tex.There we are providing
// like if it is empty show all the fields or else show the field which that text as subpart 
//it shows even it is lowercase as we are proving it to lowercase.
                data.filter(
                    (field)=>{
                        if(searchTerm==''){
                            return field
                        }else if(field.name.toLowerCase().includes(searchTerm.toLowerCase())){
                            return field
                        }
                    }
                ).map((field) => (
                        <Link to={`/fielddetails/${field.id}`} style={{textDecoration:'none'}}>
                            <Field className='field'
                            stack_image='https://p1.hiclipart.com/preview/697/161/312/button-ui-app-one-green-android-icon-png-clipart.jpg'
                            name={field.name}
                            />
                        </Link>
                ))
            }
            </div>

            <div className='upcomming Events'>
            <img href='https://avatars.githubusercontent.com/u/73348574?s=460&u=b53b92d1b2e3f3a270fc301ee11d3a5e963910d2&v=4'/>
            </div>
        </div>
    )
}

export default Home
