import React from 'react'
import Field from './Field'
import './Fielddetails.css'
import Member from './Member'
import {useState,useEffect} from 'react'


function Fielddetails({field}) {
    const [data, setData] = useState([]);

    var temp =  '{"query":"{categoryByName(name:"'+{field}+'"){members{name,intro}}}"';

    useEffect(() => {
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: temp,
        };

         fetch('http://127.0.0.1:8000/graphql', sendingPost)
            .then(response => response.json())
            .then(data => setData(data.data.categoryByName.members));
    },[])

    let board = []
    for(let row=0;row<data.length;row++){
        board[row]=
        <Member
            name={data[row]}
            image='https://www.w3schools.com/w3css/img_avatar3.png'
            intro='OpenSource Enthusiast,Web Developer,Crazy about StartUps'/>
    }

    return (
        <div className='members'>      //{Field}

            <Member
            name='Pranav'
            image='https://www.w3schools.com/w3css/img_avatar3.png'
            intro='OpenSource Enthusiast,Web Developer,Crazy about StartUps'/>
            <Member
            name='Pranav'
            image='https://www.w3schools.com/w3css/img_avatar3.png'
            intro='OpenSource Enthusiast,Web Developer,Crazy about StartUps'/>
            <Member
            name='Pranav'
            image='https://www.w3schools.com/w3css/img_avatar3.png'
            intro='OpenSource Enthusiast,Web Developer,Crazy about StartUps'/>
        </div>
    )
}

export default Fielddetails
