import React from 'react'
import Field from './Field'
import './Fielddetails.css'
import Member from './Member'
import {useState,useEffect} from 'react'
import { Link, useParams } from 'react-router-dom'


function Fielddetails() {

    const {id} = useParams();

    const [data, setData] = useState([]);
    //Now what i shoud do is i should make the name of the filed as dynamic so that we can access the Opensourse or Amhacks or CP anything.

    //Now we can add this query dynamically by that dollar and it should be enclosed in semicolon bcz i had seen an error in netwok tab of inspect saying that we need string.

    {/* Here i had */}
    const query = `query{
        categoryById(id:${id}){
          members{
           id
           name
           image
           intro
           description
           githubusername
           discordusername
           email
           phone
         }
       }
     }`


    useEffect(() => {
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },

            body: JSON.stringify({query}),
        };

         fetch('http://127.0.0.1:8000/graphql', sendingPost)
            .then(response => response.json())
            .then(data => setData(data.data.categoryById.members));
    },[])

    console.log(data);

    return (

        <div className='members'>
            {
                data.map((member) => (
                    <div className='member-preview' key={member.id}>
                    <Link to={`/members/${member.id}`}>
                    <Member
                    name={member.name}
                    image='https://www.w3schools.com/w3css/img_avatar3.png'
                    intro={member.intro}
                    />
                    </Link>
                    </div>
                ))
            }
        </div>
    )
}

export default Fielddetails
